mapper.select:
select ut.*,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id, urt.create_dt as urt_create_dt, urt.update_dt as urt_update_dt,
rt.* from user_tb ut join user_role_tb urt on (ut.user_id = urt.user_id) join role_tb rt on (urt.role_id = rt.role_id) where ut.user_id = #{userId};

select ut.*,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id, urt.create_dt as urt_create_dt, urt.update_dt as urt_update_dt,
rt.* from user_tb ut join user_role_tb urt on (ut.user_id = urt.user_id) join role_tb rt on (urt.role_id = rt.role_id) where ut.email = #{email};

select ut.*,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id, urt.create_dt as urt_create_dt, urt.update_dt as urt_update_dt,
rt.* from user_tb ut join user_role_tb urt on (ut.user_id = urt.user_id) join role_tb rt on (urt.role_id = rt.role_id) where ut.username = #{username};

select ut.*,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id, urt.create_dt as urt_create_dt, urt.update_dt as urt_update_dt,
rt.* from user_tb ut join user_role_tb urt on (ut.user_id = urt.user_id) join role_tb rt on (urt.role_id = rt.role_id) where urt.role_id = 2 or urt.role_id = 3;
